USE test_auction;
CREATE TABLE IF NOT EXISTS USER (id INT NOT NULL AUTO_INCREMENT, username varchar(255) NOT NULL UNIQUE, password varchar(255) NOT NULL, PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS LEAGUE (id INT NOT NULL AUTO_INCREMENT, league_name varchar(255) NOT NULL UNIQUE, num_teams INT NOT NULL, num_positions INT NOT NULL, salary_cap INT NOT NULL, draft_type ENUM('AUCTION', 'SNAKE'), PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS PLAYER_POSITION (id INT NOT NULL AUTO_INCREMENT, position_name varchar(255) NOT NULL UNIQUE, PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS TEAM (id INT NOT NULL AUTO_INCREMENT, team_name varchar(255) NOT NULL UNIQUE, user_id INT, league_id INT, PRIMARY KEY (id, user_id, league_id), FOREIGN KEY (league_id) REFERENCES LEAGUE(id) ON DELETE CASCADE, FOREIGN KEY (user_id) REFERENCES USER(id) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS PLAYER (id INT NOT NULL AUTO_INCREMENT, first_name varchar(255) NOT NULL UNIQUE, last_name varchar(255) NOT NULL UNIQUE, position_id INT, team varchar(255) NOT NULL, default_value INT, PRIMARY KEY (id), FOREIGN KEY (position_id) REFERENCES PLAYER_POSITION(id) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS TEAM_PLAYER (team_id INT, player_id INT, cost INT NOT NULL, PRIMARY KEY (team_id, player_id), FOREIGN KEY (team_id) REFERENCES TEAM(id) ON DELETE CASCADE, FOREIGN KEY (player_id) REFERENCES PLAYER(id) ON DELETE CASCADE);
DELETE FROM PLAYER;